mapper.insert:
INSERT INTO board(
                   board_no
                 , board_title
                 , board_content
                 , mem_id
                 ) 
		VALUES (
		          (SELECT NVL(MAX(board_no),0) +1
		           FROM board) 
		         , #{boardTitle}
		         , #{boardContent}
		         , #{memId}
		         )
