mapper.insert:
INSERT INTO board(
                   board_no
                 , board_title
                 , board_content
                 , mem_id
                 ) 
		VALUES (
		          (SELECT NVL(MAX(board_no),0) +1
		           FROM board) 
		         , #{boardTitle}
		         , #{boardContent}
		         , #{memId}
		         )

mapper.update:
UPDATE board
SET board_title = #{boardTitle}
   ,board_content = #{boardContent}
   ,update_date   = SYSDATE
WHERE board_no = #{boardNo}

UPDATE board
SET    del_yn = 'Y'
WHERE board_no = #{boardNo}
